const leftpanel = document.getElementById('leftpanel');
const rightpanel = document.getElementById('rightpanel');
const content = [
    '',
    ''
];
const messages = [
    "Injecting malware",
    "Decrypting passwords",
    "Exploiting vulnerabilities",
    "Escalating privileges",
    "Evading detection",
    "Masking identity",
    "Hijacking sessions",
    "Spoofing IP",
    "Keylogging activity",
    "DDoS attacks",
    "Accessing Account",
];
let contentIndex = 0;

// Function to switch side panel content
const switchContent = () => {
    leftpanel.innerHTML = content[contentIndex];
    rightpanel.innerHTML = content[contentIndex];
    contentIndex = (contentIndex + 1) % content.length; 
}
setInterval(switchContent, 100);

// Function to display messages with loading percentage
const displayMessages = (index, percent = 0) => {
    if (index < messages.length) {
        text.innerHTML = messages[index] + "...." + percent + "%";
        setTimeout(() => {
            if (percent < 100) {
                displayMessages(index, percent + 5);
            } else {
                if (index == messages.length - 1) {
                    document.getElementById('container').style.display = 'none';
                    document.getElementById('warning').style.display = 'block';
                } else { displayMessages(index + 1); }
            }
        }, 100);
    }
}
displayMessages(0);
